import {React,  useState, useEffect } from 'react';

function Menue(props) {


  const lunch = localStorage.getItem("lunch")

  const breakfast = localStorage.getItem("breakfast")

  const dinner = localStorage.getItem("dinner")

    const checkNull=(val)=> {
      if (val === null) {
     return 0
      }else{
        return JSON.parse(val)
      }
    }
    const lunchCal =   checkNull(lunch)
    const breakfastCal = checkNull(breakfast)
    const dinnercal = checkNull(dinner)
    const total = breakfastCal + lunchCal +dinnercal;

    return (
        <div>
             <div>
             {breakfast != null &&
      

<div class="card cardOne">
<div class="card-header">Breakfast</div>
  <img src= {breakfast.image}  alt="" style={{width: "290px", height:"300px" }} /> 
  <div class="card-body">
      <h5>{breakfast.label}</h5>
    <p> cuisine Type: {breakfast.cuisineType}</p>
    <p> Dish Type {breakfast.dishType}</p>
    <p> calories {breakfast.calories}</p>
  
</div>
</div>

      }

             {lunch != null &&
<div class="card cardOne">
<div class="card-header">Lunch</div>
  <img src= {lunch.image}  alt="" style={{width: "290px", height:"300px" }} /> 
  <div class="card-body">
      <h5>{lunch.label}</h5>
    <p> cuisine Type: {lunch.cuisineType}</p>
    <p> Dish Type {lunch.dishType}</p>
    <p> calories {lunch.calories}</p>

  </div>
</div>

      }




{dinner != null &&
<div class="card cardOne">
<div class="card-header">Dinner</div>
  <img src= {dinner.image}  alt="" style={{width: "290px", height:"300px" }} /> 
  <div class="card-body">
      <h5>{dinner.label}</h5>
    <p> cuisine Type: {dinner.cuisineType}</p>
    <p> Dish Type {dinner.dishType}</p>
    <p> calories {dinner.calories}</p>

  </div>
  
</div>

      }
        </div>

        <div className ="footer">
          <h2> The total  calories is {total} </h2>
        </div>
        </div>
    );
}

export default Menue;


